    /**
     * Transforms a ResultSet iterating on the sm_tb_flower_intor on a SmTbFlowerIntorBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting SmTbFlowerIntorBean pObject
     */
    //72
    public SmTbFlowerIntorBean decodeRow(ResultSet rs) throws SQLException
    {
        SmTbFlowerIntorBean pObject = createSmTbFlowerIntorBean();
        pObject.setItemSeq(Manager.getLong(rs, 1));
        pObject.setTitle(rs.getString(2));
        pObject.setItemCharacter(rs.getString(3));
        pObject.setUsages(rs.getString(4));
        pObject.setDescription(rs.getString(5));
        pObject.setItemPics(rs.getString(6));
        pObject.setIsShow(rs.getString(7));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the sm_tb_flower_intor table on a SmTbFlowerIntorBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting SmTbFlowerIntorBean pObject
     */
    //73
    public SmTbFlowerIntorBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        SmTbFlowerIntorBean pObject = createSmTbFlowerIntorBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_ITEM_SEQ:
                    ++pos;
                    pObject.setItemSeq(Manager.getLong(rs, pos));
                    break;
                case ID_TITLE:
                    ++pos;
                    pObject.setTitle(rs.getString(pos));
                    break;
                case ID_ITEM_CHARACTER:
                    ++pos;
                    pObject.setItemCharacter(rs.getString(pos));
                    break;
                case ID_USAGES:
                    ++pos;
                    pObject.setUsages(rs.getString(pos));
                    break;
                case ID_DESCRIPTION:
                    ++pos;
                    pObject.setDescription(rs.getString(pos));
                    break;
                case ID_ITEM_PICS:
                    ++pos;
                    pObject.setItemPics(rs.getString(pos));
                    break;
                case ID_IS_SHOW:
                    ++pos;
                    pObject.setIsShow(rs.getString(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

