    /**
     * Transforms a ResultSet iterating on the sm_tb_type_grp table on a SmTbTypeGrpBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting SmTbTypeGrpBean pObject
     */
    //73
    public SmTbTypeGrpBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        SmTbTypeGrpBean pObject = createSmTbTypeGrpBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_PRD_TYPE_ID:
                    ++pos;
                    pObject.setPrdTypeId(Manager.getLong(rs, pos));
                    break;
                case ID_PRD_TYPE_NAME:
                    ++pos;
                    pObject.setPrdTypeName(rs.getString(pos));
                    break;
                case ID_PRD_TYPE_DESC:
                    ++pos;
                    pObject.setPrdTypeDesc(rs.getString(pos));
                    break;
                case ID_PRD_TYPE_DISCOUNT:
                    ++pos;
                    pObject.setPrdTypeDiscount(Manager.getLong(rs, pos));
                    break;
                case ID_OWNER:
                    ++pos;
                    pObject.setOwner(Manager.getLong(rs, pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

