    /**
     * Transforms a ResultSet iterating on the sm_tb_week_discount table on a SmTbWeekDiscountBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting SmTbWeekDiscountBean pObject
     */
    //73
    public SmTbWeekDiscountBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        SmTbWeekDiscountBean pObject = createSmTbWeekDiscountBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_DIS_SEQ:
                    ++pos;
                    pObject.setDisSeq(Manager.getLong(rs, pos));
                    break;
                case ID_PRD_ID:
                    ++pos;
                    pObject.setPrdId(Manager.getLong(rs, pos));
                    break;
                case ID_DIS_PRICE:
                    ++pos;
                    pObject.setDisPrice(Manager.getLong(rs, pos));
                    break;
                case ID_PRD_TOTAL:
                    ++pos;
                    pObject.setPrdTotal(Manager.getLong(rs, pos));
                    break;
                case ID_PRD_CURRENT_TOTAL:
                    ++pos;
                    pObject.setPrdCurrentTotal(Manager.getLong(rs, pos));
                    break;
                case ID_START_DATE:
                    ++pos;
                    pObject.setStartDate(rs.getTimestamp(pos));
                    break;
                case ID_END_DATE:
                    ++pos;
                    pObject.setEndDate(rs.getTimestamp(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

