    //72
    public SmTbSaleItemLogBean decodeRow(ResultSet rs) throws SQLException
    {
        SmTbSaleItemLogBean pObject = createSmTbSaleItemLogBean();
        pObject.setIdx(Manager.getInteger(rs, 1));
        pObject.setItemSeq(Manager.getLong(rs, 2));
        pObject.setItemLog(rs.getString(3));
        pObject.setCreateUser(Manager.getLong(rs, 4));
        pObject.setCreateDate(rs.getDate(5));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the sm_tb_sale_item_log table on a SmTbSaleItemLogBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting SmTbSaleItemLogBean pObject
     */
    //73
    public SmTbSaleItemLogBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        SmTbSaleItemLogBean pObject = createSmTbSaleItemLogBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_IDX:
                    ++pos;
                    pObject.setIdx(Manager.getInteger(rs, pos));
                    break;
                case ID_ITEM_SEQ:
                    ++pos;
                    pObject.setItemSeq(Manager.getLong(rs, pos));
                    break;
                case ID_ITEM_LOG:
                    ++pos;
                    pObject.setItemLog(rs.getString(pos));
                    break;
                case ID_CREATE_USER:
                    ++pos;
                    pObject.setCreateUser(Manager.getLong(rs, pos));
                    break;
                case ID_CREATE_DATE:
                    ++pos;
                    pObject.setCreateDate(rs.getDate(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

