    /**
     * Looks for the number of elements of a specific SysTbPropertiesBean pObject given a c
     *
     * @param pObject the SysTbPropertiesBean pObject to look for
     * @return the number of rows returned
     */
    //83
    public int countUsingTemplate(SysTbPropertiesBean pObject) throws SQLException
    {
        StringBuffer where = new StringBuffer("");
        Connection c = null;
        PreparedStatement ps = null;
        StringBuffer _sql = null;
        StringBuffer _sqlWhere = null;

        try
        {
                _sql = new StringBuffer("SELECT count(*) as MCOUNT  from sys_tb_properties WHERE ");
                _sqlWhere = new StringBuffer("");
                int _dirtyCount = 0;

                if (pObject.isIdxModified()) {
                    _dirtyCount++;
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("idx= ?");
                }

                if (pObject.isKeyNameModified()) {
                    _dirtyCount++;
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("key_name= ?");
                }

                if (pObject.isKeyValueModified()) {
                    _dirtyCount++;
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("key_value= ?");
                }

                if (pObject.isKeyGroupModified()) {
                    _dirtyCount++;
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("key_group= ?");
                }

                if (pObject.isDescriptionModified()) {
                    _dirtyCount++;
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("description= ?");
                }

                if (pObject.isModifyDateModified()) {
                    _dirtyCount++;
                    _sqlWhere.append((_sqlWhere.length() == 0) ? " " : " AND ").append("modify_date= ?");
                }

                if (_dirtyCount == 0)
                   throw new SQLException ("The pObject to look is unvalid : not initialized !");

                _sql.append(_sqlWhere);
                c = getConnection();
                ps = c.prepareStatement(_sql.toString(),ResultSet.TYPE_SCROLL_INSENSITIVE, ResultSet.CONCUR_READ_ONLY);

                _dirtyCount = 0;

                if (pObject.isIdxModified()) {
                    Manager.setInteger(ps, ++_dirtyCount, pObject.getIdx());
                }

                if (pObject.isKeyNameModified()) {
                    ps.setString(++_dirtyCount, pObject.getKeyName());
                }

                if (pObject.isKeyValueModified()) {
                    ps.setString(++_dirtyCount, pObject.getKeyValue());
                }

                if (pObject.isKeyGroupModified()) {
                    ps.setString(++_dirtyCount, pObject.getKeyGroup());
                }

                if (pObject.isDescriptionModified()) {
                    ps.setString(++_dirtyCount, pObject.getDescription());
                }

                if (pObject.isModifyDateModified()) {
                    if (pObject.getModifyDate() == null) ps.setNull(++_dirtyCount, Types.DATE); else ps.setDate(++_dirtyCount, new java.sql.Date(pObject.getModifyDate().getTime()));
                }

                return countByPreparedStatement(ps);
        }
        finally
        {
            getManager().close(ps);
            freeConnection(c);
        }
    }



