    /**
     * Transforms a ResultSet iterating on the sys_tb_properties on a SysTbPropertiesBean pObject.
     *
     * @param rs the ResultSet to be transformed
     * @return pObject resulting SysTbPropertiesBean pObject
     */
    //72
    public SysTbPropertiesBean decodeRow(ResultSet rs) throws SQLException
    {
        SysTbPropertiesBean pObject = createSysTbPropertiesBean();
        pObject.setIdx(Manager.getInteger(rs, 1));
        pObject.setKeyName(rs.getString(2));
        pObject.setKeyValue(rs.getString(3));
        pObject.setKeyGroup(rs.getString(4));
        pObject.setDescription(rs.getString(5));
        pObject.setModifyDate(rs.getDate(6));

        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

    /**
     * Transforms a ResultSet iterating on the sys_tb_properties table on a SysTbPropertiesBean pObject according to a list of fields.
     *
     * @param rs the ResultSet to be transformed
     * @param fieldList table of the field's associated constants
     * @return pObject resulting SysTbPropertiesBean pObject
     */
    //73
    public SysTbPropertiesBean decodeRow(ResultSet rs, int[] fieldList) throws SQLException
    {
        SysTbPropertiesBean pObject = createSysTbPropertiesBean();
        int pos = 0;
        for(int i = 0; i < fieldList.length; i++)
        {
            switch(fieldList[i]) {
                case ID_IDX:
                    ++pos;
                    pObject.setIdx(Manager.getInteger(rs, pos));
                    break;
                case ID_KEY_NAME:
                    ++pos;
                    pObject.setKeyName(rs.getString(pos));
                    break;
                case ID_KEY_VALUE:
                    ++pos;
                    pObject.setKeyValue(rs.getString(pos));
                    break;
                case ID_KEY_GROUP:
                    ++pos;
                    pObject.setKeyGroup(rs.getString(pos));
                    break;
                case ID_DESCRIPTION:
                    ++pos;
                    pObject.setDescription(rs.getString(pos));
                    break;
                case ID_MODIFY_DATE:
                    ++pos;
                    pObject.setModifyDate(rs.getDate(pos));
                    break;
            }
        }
        pObject.isNew(false);
        pObject.resetIsModified();

        return pObject;
    }

